/* CSS */
p {
  color: blue;
}
#main-title {
  font-size: 2rem;
}
.highlight {
  background: yellow;
}

/* CSS Colors */
.box {
  color: #50FA7B;              /* Hex */
  background: rgb(80,250,123);  /* RGB */
  border: 2px solid hsl(134, 95%, 65%); /* HSL */
  opacity: 0.9;                /* Transparency */
}

/* Typography */
.text {
  font-family: 'Arial', sans-serif;
  font-size: 1.2rem;
  font-weight: bold;
  line-height: 1.6;
  text-align: center;
  text-transform: uppercase;
  letter-spacing: 2px;
}

/* Box Model */
.container {
  width: 200px;
  height: 200px;
  padding: 20px;
  border: 5px solid #50FA7B;
  margin: 15px;
  box-sizing: border-box;
}

/* Flexbox */
.flex-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 10px;
}

/* Grid */
.grid-container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

/* Responsive Design */
.card {
  width: 300px;
  margin: 20px;
}

@media (max-width: 768px) {
  .card {
    width: 100%;
    margin: 10px;
  }
}

/* Animation */
@keyframes bounce {
  0% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
  100% { transform: translateY(0); }
}

.animated {
  animation: bounce 1s infinite;
  transition: all 0.3s ease;
}
